import * as React from 'react';
import { View, Text } from "react-native";

import { Button } from '@rneui/themed';


import * as WebBrowser from 'expo-web-browser';
import { makeRedirectUri, useAuthRequest } from 'expo-auth-session';


WebBrowser.maybeCompleteAuthSession();

const discovery = {
    authorizationEndpoint: 'https://accounts.spotify.com/authorize',
    tokenEndpoint: 'https://accounts.spotify.com/api/token',
  };


const getTokens = async (credentials, authorizationCode) => {
    const credsB64 = btoa(`${credentials.clientId}:${credentials.clientSecret}`);

    const response = await fetch('https://accounts.spotify.com/api/token', {
      method: 'POST',
      headers: {
        Authorization: `Basic ${credsB64}`,
        'Content-Type': 'application/x-www-form-urlencoded',
      },
      body: `grant_type=authorization_code&code=${authorizationCode}&redirect_uri=${
        credentials.redirectUri
      }`,
    });
    console.log("HI2")
    const responseJson =  response.json();
}

export default function LoginScreen(code) {


    const getAndSetTokens = (code) => {
        try {
            getSpotifyCredentials().then(credentials => {
                getTokens(credentials, code).then(responseJson => {
                    const {
                        access_token: accessToken,
                        refresh_token: refreshToken,
                        expires_in: expiresIn,
                      } = responseJson;

                      const expirationTime = new Date().getTime() + expiresIn * 1000;
                      console.log("RESSSS")
                      console.log(responseJson)
                })
            })

        } catch (err) {
          console.error(err);
        }
      }


const [request, response, promptAsync] = useAuthRequest(
    {
        clientId: '2032e93e5d6b4466a81a2fd6152cd0a6',
        scopes: ['user-read-email', 'playlist-modify-public'],
        // In order to follow the "Authorization Code Flow" to fetch token after authorizationEndpoint
        // this must be set to false
        usePKCE: false,
        redirectUri: "http://127.0.0.1:19006"
    },
    discovery
    );

  React.useEffect(() => {
    if (response?.type === 'success') {
      const { code } = response.params;
    //   console.log("CODE")
    //   console.log(code)
        getAndSetTokens(code)
    }
  }, [response]);





   return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>

        <Button
        title="Log In"
        onPress={() => {
            promptAsync();
        }}
        type="outline"
        /> 


</View>
   );
 }
